workflow-item.script:
var restHost = RESTHostManager.createHost("TransientRESTHost-" + netboxRestHost);
var transientRestHost = RESTHostManager.createTransientHostFrom(restHost);
transientRestHost.url = netboxRestHost;
nbresthost = transientRestHost;

// check if ip already present
var request = nbresthost.createRequest("GET", netboxIpPath + "?q=" + ipAddrData.ip+"/"+ipAddrData.cdir);
request.setHeader("Content-Type", "application/json");
request.setHeader("Accept", "application/json");
request.setHeader("Authorization", "Token " + netboxToken);
var response = request.execute();

System.log("******Rest Response***** ");
statusCode = response.statusCode;

System.log("Status code: " + statusCode);

if (statusCode != 200 && statusCode != 201 && statusCode != 202 && statusCode != 203 && statusCode != 204) {
    throw "HTTP " + statusCode + " - " + statusMessage + " : " + response.contentAsString;
    System.log("****All Headers****");
}

var ipcheckJSON = JSON.parse(response.contentAsString);
if (ipcheckJSON.count > 0) {
    isPresent = true;
    netboxIpId = ipcheckJSON.results[0].id;
}
else {
    isPresent = false;
    netboxIpId = null;
}

var restHost = RESTHostManager.createHost("TransientRESTHost-" + netboxRestHost);
var transientRestHost = RESTHostManager.createTransientHostFrom(restHost);
transientRestHost.url = netboxRestHost;
var nbresthost = transientRestHost;

if (isPresent) {
    // update ip record in netbox
    //var request = restClient.createRequest(operation, url, content);
    var mutotext = {
        "status": "active",
        "description": fqdn,
        "dns_name": fqdn
    };
    var content = JSON.stringify(mutotext);
    System.log("*** Update content: " + content);

    var tmpurl = netboxIpPath + netboxIpId + "/";
    var request = nbresthost.createRequest("PATCH", tmpurl, content);
    request.setHeader("Content-Type", "application/json");
    request.setHeader("Accept", "application/json");
    request.setHeader("Authorization", "Token " + netboxToken);
    var response = request.execute();

    System.log("******Rest Response***** ");
    statusCode = response.statusCode;
    System.log("Status code: " + statusCode);
    if (statusCode != 200 && statusCode != 201 && statusCode != 202 && statusCode != 203 && statusCode != 204)
        throw "HTTP " + statusCode + " - " + statusMessage + " : " + response.contentAsString;
    System.log("****All Headers****");
}
else {
    // create ip record in netbox
    var ipa = ipAddrData.ip + "/" + ipAddrData.cdir;
    var vrfid = ipAddrData.vrfid;
    var mutotext = {
        "address": ipa,
        "status": "active",
        "description": fqdn,
        "dns_name": fqdn,
        "vrf": vrfid
    };
    var content = JSON.stringify(mutotext);
    System.log("*** Create content: " + content);

    var request = nbresthost.createRequest("POST", netboxIpPath, content);
    request.setHeader("Content-Type", "application/json");
    request.setHeader("Accept", "application/json");
    request.setHeader("Authorization", "Token " + netboxToken);
    var response = request.execute();

    System.log("******Rest Response***** ");
    statusCode = response.statusCode;
    System.log("Status code: " + statusCode);
    if (statusCode != 200 && statusCode != 201 && statusCode != 202 && statusCode != 203 && statusCode != 204)
        throw "HTTP " + statusCode + " - " + statusMessage + " : " + response.contentAsString;
    System.log("****All Headers****");
}
