workflow-item.script:
var restHost = RESTHostManager.createHost("TransientRESTHost-" + netboxRestHost);
var transientRestHost = RESTHostManager.createTransientHostFrom(restHost);
transientRestHost.url = netboxRestHost;
nbresthost = transientRestHost;

// check if vm already present
var request = nbresthost.createRequest("GET", netboxVmPath + "?q=" + vmName);
request.setHeader("Content-Type", "application/json");
request.setHeader("Accept", "application/json");
request.setHeader("Authorization", "Token " + netboxToken);

System.log(netboxVmPath + "?q=" + vmName);
System.log("Token " + netboxToken);

var response = request.execute();

System.log("******Rest Response***** ");
statusCode = response.statusCode;
System.log("Status code: " + statusCode);
statusMessage = response.statusMessage;
System.log("Status Message: " + statusMessage);

// Not the fastest but display nicely the JSON : better for learning and troubleshooting purposes.
// if (response.contentAsString != "") System.log("Content as string: " + JSON.stringify(JSON.parse(response.contentAsString), null, 2));

if (statusCode != 200 && statusCode != 201 && statusCode != 202 && statusCode != 203 && statusCode != 204) {
    throw "HTTP " + statusCode + " - " + statusMessage + " : " + response.contentAsString;
    System.log("****All Headers****");
}

var vmcheckJSON = JSON.parse(response.contentAsString);

if (vmcheckJSON.count > 0) {
    netboxVmId = vmcheckJSON.results[0].id;
    System.log("******** vmid - " + netboxVmPath + netboxVmId);
    var request = nbresthost.createRequest("DELETE", netboxVmPath + netboxVmId + "/");
    request.setHeader("Content-Type", "application/json");
    request.setHeader("Accept", "application/json");
    request.setHeader("Authorization", "Token " + netboxToken);

    var response = request.execute();

    System.log("******Rest Response***** ");
    statusCode = response.statusCode;
    System.log("Status code: " + statusCode);
    statusMessage = response.statusMessage;
    System.log("Status Message: " + statusMessage);

    // Not the fastest but display nicely the JSON : better for learning and troubleshooting purposes.
    if (response.contentAsString != "") System.log("Content as string: " + JSON.stringify(JSON.parse(response.contentAsString), null, 2));

    if (statusCode != 200 && statusCode != 201 && statusCode != 202 && statusCode != 203 && statusCode != 204) {
        throw "HTTP " + statusCode + " - " + statusMessage + " : " + response.contentAsString;
        System.log("****All Headers****");
    }
}
else {

    netboxIpId = null;
}
